CREATE TABLE menu_items (
    menu_item_id INT PRIMARY KEY,
    menu_item_name VARCHAR(100),
    menu_item_type VARCHAR(100),
    menu_item_description VARCHAR(200),
    menu_item_spice BOOLEAN,
    menu_item_woksmart BOOLEAN,
    menu_item_calories INT,
    menu_price_small FLOAT,
    menu_price_medium FLOAT,
    menu_price_large FLOAT,
    menu_price_bowl FLOAT,
    menu_price_plate FLOAT,
    menu_price_bplate FLOAT,
    menu_item_status BOOLEAN
);

